import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';


const UserList = () => {
    const [users, setUsers] = useState([]);
    const navigate= useNavigate();
    const registerUser = () => {
        try {
            navigate('/users');
        } catch (err) {
            console.error(err.message);
        }
    }
    const deleteUser= async(id) => {
        try{
            const deleteUser= await fetch(`http://localhost:3001/users/${id}`, {
                method: 'DELETE'
            });
        setUsers(users.filter(user => user.user_id !== id))
        }catch(err){
            console.error(err.message)
        }
    }


    const update= async(id) => {
        try{
            const deleteUser= await fetch(`http://localhost:3001/users/${id}`, {
                method: 'DELETE'
            });
        setUsers(users.filter(user => user.user_id !== id))
        }catch(err){
            console.error(err.message)
        }
    }
    const getUser = async () => {
        try {
            const response = await fetch('http://localhost:3001/users');
            const jsonData = await response.json();
            if (jsonData && jsonData.data && Array.isArray(jsonData.data.passengers))
                setUsers(jsonData.data.passengers);
        } catch (err) {
            console.error(err.message);
        }
    };

    useEffect(() => {
        getUser();
    }, []);

    return (
        <div className='top-spacing mb-3'>
        <button className='btn btn-success ' onClick={ ()=> registerUser()}>Register</button>
        
        <div className='list-group'>
            <table className="table table-hover table-dark top-spacing mb-2">
                <thead>
                    <tr className='bg-primary'>
                        <th scope="col">Name</th>
                        <th scope="col">Email</th>
                        <th scope="col">Phone Number</th>
                        <th scope="col">NID</th>
                        <th scope="col">Gender</th>
                        <th scope="col">Edit</th>
                        <th scope="col">Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.user_id}>
                            <td>{user.username}</td>
                            <td>{user.email}</td>
                            <td>{user.phone}</td>
                            <td>{user.nid}</td>
                            <td>{user.gender}</td>
                            <td><button className="btn btn-warning" onClick={() => deleteUser(user.user_id)}>Update</button></td>
                            <td><button className="btn btn-danger" onClick={() => deleteUser(user.user_id)}>Delete</button></td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
        </div>
    );
};

export default UserList;
