import React, {useState, Fragment} from 'react';

import './style.css';

const AddUser = () => {
    const[description, setDescription]= useState("");
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [nid, setNid] = useState("");
    const [gender, setGender] = useState("1");
    const onSubmitForm = async(e)=>{
        e.preventDefault();
        try{
            const body = { name, email, nid, gender };
            const response= await fetch("http://localhost:3000/", {
                method: "POST",
                headers: {"Content-Type": "appliction/json"},
                body: JSON.stringify(body)
            });
            console.log(response);
        }catch(err){
            console.error(err.message);
        }

    }
    return (
        <Fragment>
        <div className='top-spacing mb-3'>
            <form action="" onSubmit={onSubmitForm}>
                <div className="form-row">
                    <div className='col-md-6 mb-2 '>
                        <input type="text" className='form-control' placeholder='Name' value={description} onChange={e=> setDescription(e.target.value)}/>
                    </div>
                    <div className='col-md-6 mb-2'>
                        <input type="text" className='form-control' placeholder='Email' value={description}/>
                    </div>
                    <div className='col-md-6 mb-2'>
                        <input type="text" className='form-control' placeholder='NID' value={description}/>
                    </div>
                    <div className='col-md-6 mb-2'>
                        <select className='custom-select my-10 mr-sm-12'>
                            <option value='1'>Male</option>
                            <option value='2'>Female</option>
                            <option value='3'>Other</option>
                        </select>
                    </div>
                    <button className="btn btn-primary">Register User</button>
                </div>

            </form>


        </div>
        </Fragment>
        )
    
}

export default AddUser
